# coding: utf-8
from itertools import permutations
from collections import defaultdict

class P438FindAllAnagramsInAString(object):
    def findAnagrams2(self, s, p):
        """
        :type s: str
        :type p: str
        :rtype: List[int]
        """
        result = list()
        if len(s) == 0 or len(p) == 0 or len(s) < len(p):
            return result

        full_permutations_of_p = set(permutations(p))

        for permutation in full_permutations_of_p:
            index = s.find(''.join(permutation))
            while index != -1:
                result.append(index)
                index = s.find(''.join(permutation), index + 1)

        return result

    def findAnagrams(self, s, p):
        """
        :type s: str
        :type p: str
        :rtype: List[int]
        """
        dict_p = defaultdict(int)
        for letter in p:
            dict_p[letter] += 1

        dict_s = dict()
        list_n = list()

        len_p = len(p)
        for index, value in enumerate(s):  # 将每个键值展开进行循环
            dict_s[value] = dict_s.get(value, 0) + 1  # 也是字符 作为key，次数作为value
            if dict_s == dict_p:  # 如果添加完以后刚好完全相同，此时刚好循环了前len_p个，index = len_p - 1，所以返回index为0
                list_n.append(index - len_p + 1)

            if index - len_p + 1 >= 0:  # 当循环次数超过len_p后，就对dict进行操作：
                dict_s[s[index-len_p+1]] = dict_s.get(s[index - len_p + 1]) - 1  # 每往后循环一次index，就减去前面掉出去的那个字符一次
                if dict_s[s[index - len_p + 1]] == 0:  # 如果在for循环一开始添加进的字符不在目标字符串中，删掉
                    del dict_s[s[index - len_p + 1]]
        return list_n

if __name__ == '__main__':
    print(P438FindAllAnagramsInAString().findAnagrams('cbaebabacd','abc'))
    print(P438FindAllAnagramsInAString().findAnagrams('abab', 'ab'))
    print(P438FindAllAnagramsInAString().findAnagrams('baa', 'aa'))
    print(P438FindAllAnagramsInAString().findAnagrams('aaaaaaaaaa', 'aaaaaaaaaaaaa'))
    print(P438FindAllAnagramsInAString().findAnagrams('', 'yqrbgjdwtcaxzsnifvhmou'))


